// filmes.component.ts

import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Filmes } from '../Filmes';
import { FilmesService } from 'src/app/filmes.service';

@Component({
  selector: 'app-filmes',
  templateUrl: './filmes.component.html',
  styleUrls: ['./filmes.component.css']
})
export class FilmesComponent implements OnInit {

  filmes: Filmes[] = [];

  constructor(private filmesService: FilmesService) { }

  ngOnInit(): void {
    this.listarFilmes();
  }

  listarFilmes(): void {
    this.filmesService.listarFilmes().subscribe(filmes => {
      this.filmes = filmes;
    });
  }

  cadastrarFilmes(filmes: Filmes[]): void {
    this.filmesService.cadastrarFilme(Filmesilmes).subscribe(() => {
      this.listarFilmes();
    });
  }

  cadastrarFilme(): void {
    const filme = new Filmes();
    filme.Nome = 'Coringa';
    filme.Ano = 2019;
    filme.Diretor = 'Todd Phillips';

    this.cadastrarFilme([filme]);
  }

}
<h1>Lista de filmes</h1>

<ul>
  <li *ngFor="let filme of filmes">
    <a [routerLink]="['/filmes', filme.id]">
      {{filmes.Nome}} 
    </a>
  </li>
</ul>

<div>
  <button (click)="cadastrarFilme(filmes)">Cadastrar filme</button>
</div>
