import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Observer } from 'rxjs';
import { FilmesService } from 'src/app/filmes.service';
import { Filmes } from 'src/app/components/';

@Component({
  selector: 'app-filmes',
  templateUrl: './filmes.component.html',
  styleUrls: ['./filmes.component.css']
})
export class FilmesComponent implements OnInit {
  novoFilmeForm: any;
  tituloFormulario: string = '';

  constructor(private filmesService: FilmesService) {}

  ngOnInit(): void {
    this.tituloFormulario = 'Novo Filme';

    this.novoFilmeForm = new FormGroup({
      Nome: new FormControl(null),
      Ano: new FormControl(null),
      Diretor: new FormControl(null),
      Duracao: new FormControl(null),
      Genero: new FormControl(null),
      Estudio: new FormControl(null),
    });
  }

  cadastrarFilme(): void {
    const filme: Filmes = this.novoFilmeForm.value as Filmes;
    const observer: Observer<Filmes> = {
      next(_result): void {
        alert('Filme cadastrado com sucesso.');
      },
      error(_error): void {
        alert('Erro ao cadastrar o filme!');
      },
      complete(): void {},
    };

    this.filmesService.cadastrarFilme(filme).subscribe(observer);
  }
}
<div class="container p-5">
  <div class="row">
    <div class="col-6 border border-light rounded p-5 shadow" *ngIf="novoFilmeForm">
      <h6>{{ tituloFormulario }}</h6>
      <form [formGroup]="novoFilmeForm" (ngSubmit)="cadastrarFilme()">
        <div class="form-group">
          <label>Nome</label>
          <input type="text" class="form-control form-control-sm" formControlName="Nome" />
        </div>
        <div class="form-group">
          <label>Ano</label>
          <input type="number" class="form-control form-control-sm" formControlName="Ano" />
        </div>
        <div class="form-group">
          <label>Diretor</label>
          <input type="text" class="form-control form-control-sm" formControlName="Diretor" />
        </div>
        <div class="form-group">
          <label>Duração</label>
          <input type="number" class="form-control form-control-sm" formControlName="Duracao" />
        </div>
        <div class="form-group">
          <label>Gênero</label>
          <input type="text" class="form-control form-control-sm" formControlName="Genero" />
        </div>
        <div class="form-group">
          <label>Estúdio</label>
          <input type="text" class="form-control form-control-sm" formControlName="Estudio" />
        </div>
        <div class="container">
          <div class="row">
            <div>
              <button type="submit" class="btn btn-sm btn-outline-secondary">Salvar</button>
            </div>
            <div class="pl-1">
              <button type="button" class="btn btn-sm btn-light">Voltar</button>
            </div>
          </div>
        </div>
      </form>
    </div>
  </div>
</div>
